unit.comment:
/**
        * An attribute. Returned by the Abdera XPath implementation when querying for Attribute nodes.
        */

unit.class:
public interface Attribute {
/** * Get the QName of the attribute * * @return The attribute QName */
QName
getQName ();
/** * Return the text value of the attribute * * @return The attribute value */
String
getText ();
/** * Set the text value of the attribute. The value will be automatically escaped for proper serialization to XML * * @param text The attribute value */
Attribute
setText (
String
text
)
;
/** * The Abdera Factory */
Factory
getFactory ();
}
