/** * Test {@link BoundedInputStream#read()}. */
public void
testReadSingle () throws
Exception
{
BoundedInputStream
bounded =
null
;
byte []
helloWorld =
"Hello World".
getBytes ()
;
byte []
hello =
"Hello".
getBytes ()
;
// limit = length
bounded = new
BoundedInputStream (
new
ByteArrayInputStream (
helloWorld
)
,
helloWorld. length
)
;
for (
int
i =
0
;
i < helloWorld. length
;
i++
) {
assertEquals (
"limit = length byte[" + i + "]"
,
helloWorld [
i
]
,
bounded. read
()
)
;
}
assertEquals (
"limit = length end"
,
-1
,
bounded. read
()
)
;
// limit > length
bounded = new
BoundedInputStream (
new
ByteArrayInputStream (
