public class FileDeleteStrategyTestCase
extends FileBasedTestCase
{
public FileDeleteStrategyTestCase (
String
name
)
{
super (
name
)
;
}
@ Override
protected void
setUp () throws
Exception
{
super. setUp
()
;
}
@ Override
protected void
tearDown () throws
Exception
{
super. tearDown
()
;
}
//-----------------------------------------------------------------------
public void
testDeleteNormal () throws
Exception
{
File
baseDir =
getTestDirectory ()
;
File
subDir =
new
File (
baseDir
,
"test"
)
;
assertTrue (
subDir. mkdir
()
)
;
File
subFile =
new
File (
subDir
,
"a.txt"
)
;
createFile (
subFile
,
16
)
;
assertTrue (
subDir. exists
()
)
;
assertTrue (
subFile. exists
()
)
;
// delete dir try {
FileDeleteStrategy.NORMAL. delete
(
subDir
)
;
fail ()
;
}
catch (
IOException
ex
) { // expected }
assertTrue (
subDir. exists
()
)
;
assertTrue (
subFile. exists
()
)
;
// delete file
FileDeleteStrategy.NORMAL. delete
(
subFile
)
;
assertTrue (
subDir. exists
()
)
;
assertFalse (
subFile. exists
()
)
;
// delete dir
FileDeleteStrategy.NORMAL. delete
(
subDir
)
;
assertFalse (
subDir. exists
()
)
;
// delete dir
FileDeleteStrategy.NORMAL. delete
(
subDir
)
;
// no error
assertFalse (
subDir. exists
()
)
;
}
public void
testDeleteQuietlyNormal () throws
Exception
{
File
baseDir =
getTestDirectory ()
;
File
subDir =
new
File (
baseDir
,
"test"
)
;
assertTrue (
subDir. mkdir
()
)
;
File
subFile =
new
File (
subDir
,
"a.txt"
)
;
createFile (
subFile
,
16
)
;
assertTrue (
subDir. exists
()
)
;
assertTrue (
subFile. exists
()
)
;
// delete dir
assertFalse (
FileDeleteStrategy.NORMAL. deleteQuietly
(
subDir
)
)
;
assertTrue (
subDir. exists
()
)
;
assertTrue (
subFile. exists
()
)
;
// delete file
assertTrue (
FileDeleteStrategy.NORMAL. deleteQuietly
(
subFile
)
)
;
assertTrue (
subDir. exists
()
)
;
assertFalse (
subFile. exists
()
)
;
// delete dir
assertTrue (
FileDeleteStrategy.NORMAL. deleteQuietly
(
subDir
)
)
;
assertFalse (
subDir. exists
()
)
;
// delete dir
assertTrue (
FileDeleteStrategy.NORMAL. deleteQuietly
(
subDir
)
)
;
// no error
assertFalse (
subDir. exists
()
)
;
}
