private
Collection <
String
>
filesToFilenames (
Collection <
File
>
files
)
{
Collection <
String
>
filenames =
new
ArrayList <
String
>
(
files. size
()
)
;
for (
File
file :
files
) {
filenames. add
(
file. getName
()
)
;
}
return
filenames
;
}
private
Collection <
String
>
filesToFilenames (
Iterator <
File
>
files
)
{
Collection <
String
>
filenames =
new
ArrayList <
String
>
()
;
while (
files. hasNext
()
)
{
filenames. add
(
files. next
()
.
getName ()
)
;
}
return
filenames
;
}
