)
throws
Exception
{ // Note. This only tests the (File, String) version if the parent of // the File passed in is not null
assertEquals (
"Filter(File) " +
filter. getClass
()
.
getName ()
+ " not " + expected + " for " + file
,
expected
,
filter. accept
(
file
)
)
;
if (
file != null &&
file. getParentFile
()
!= null
)
{
assertEquals (
"Filter(File, String) " +
filter. getClass
()
.
getName ()
+ " not " + expected + " for " + file
,
expected
,
filter. accept
(
file. getParentFile
()
,
file. getName
()
)
)
;
}
else if (
file == null
)
{
assertEquals (
"Filter(File, String) " +
filter. getClass
()
.
getName ()
+ " not " + expected + " for null"
,
expected
,
filter. accept
(
file
)
)
;
}
}
