unit.class:
public class ReflectionToStringBuilderExcludeTest
extends TestCase
{
class TestFixture {
private String
secretField =
SECRET_VALUE
;
private String
showField =
NOT_SECRET_VALUE
;
}
private static final String
NOT_SECRET_FIELD =
"showField"
;
private static final String
NOT_SECRET_VALUE =
"Hello World!"
;
private static final String
SECRET_FIELD =
"secretField"
;
private static final String
SECRET_VALUE =
"secret value"
;
public void
test_toStringExclude () {
String
toString =
ReflectionToStringBuilder. toStringExclude
(
new
TestFixture ()
,
SECRET_FIELD
)
;
this. validateSecretFieldAbsent
(
toString
)
;
}
public void
test_toStringExcludeArray () {
String
toString =
ReflectionToStringBuilder. toStringExclude
(
new
TestFixture ()
,
new
String []
{
SECRET_FIELD
}
)
;
this. validateSecretFieldAbsent
(
toString
)
;
}
public void
test_toStringExcludeArrayWithNull () {
String
toString =
ReflectionToStringBuilder. toStringExclude
(
new
TestFixture ()
,
new
String []
{
null
}
)
;
this. validateSecretFieldPresent
(
toString
)
;
}
public void
test_toStringExcludeArrayWithNulls () {
String
toString =
ReflectionToStringBuilder. toStringExclude
(
new
TestFixture ()
,
new
String []
{
null
,
null
}
)
;
this. validateSecretFieldPresent
(
toString
)
;
}
public void
test_toStringExcludeCollection () {
List
excludeList =
new
ArrayList ()
;
excludeList. add
(
SECRET_FIELD
)
;
String
toString =
ReflectionToStringBuilder. toStringExclude
(
new
TestFixture ()
,
excludeList
)
;
this. validateSecretFieldAbsent
(
toString
)
;
}
public void
test_toStringExcludeCollectionWithNull () {
List
excludeList =
new
ArrayList ()
;
excludeList. add
(
null
)
;
String
toString =
ReflectionToStringBuilder. toStringExclude
(
