unit.class:
public
class StyleManager
{



                private
                java.util.

                        List
                        <

                                CSSRule

                            >




            rules

        ;



        public
        StyleManager
        ()
        {


                    rules
                    = new


                            ArrayList
                            <

                                    CSSRule

                                >


                        ()


                ;

            }





            public
            void

        add
        (



                        CSSRule

                    rule


            )

        {



                        rules.
                            add

                        (


                                    rule


                            )



                ;

            }





            public
            void

        applyStylesTo
        (



                        Element

                    elem


            )

        {
            for (



                            CSSRule

                        rule :


                                rules




                )
                {
                    if
                        (


                                    rule.
                                        matches

                                    (


                                                elem


                                        )



                            )


                            {



                                            rule.
                                                apply

                                            (


                                                        elem


                                                )



                                    ;

                                }



                    }


            }




            public
            void

        applyStylesTo
        (



                        IXMLElement

                    elem


            )

        {
            for (



                            CSSRule

                        rule :

                            new


                                        ReversedList
                                        <

                                                CSSRule

                                            >


                                    (


                                                rules


                                        )






                )
                {
                    if
                        (


                                    rule.
                                        matches

                                    (


                                                elem


                                        )



                            )


                            {
                                //System.out.println("StyleManager applying "+rule+" to
                                    "+elem);




                                            rule.
                                                apply

                                            (


                                                        elem


                                                )



                                    ;

                                }



                    }


            }


    }
