class BeanGen {
/** Constructor style for none. */
static final int
CONSTRUCTOR_NONE =
0
;
/** Constructor style for builder-based. */
static final int
CONSTRUCTOR_BY_BUILDER =
1
;
/** Constructor style for argument-based. */
static final int
CONSTRUCTOR_BY_ARGS =
2
;
/** Start marker. */
private static final String
AUTOGENERATED_START_TEXT =
"AUTOGENERATED START"
;
/** Start marker. */
private static final String
AUTOGENERATED_START =
"\t//------------------------- AUTOGENERATED START -------------------------"
;
/** End marker. */
private static final String
AUTOGENERATED_END =
"\t//-------------------------- AUTOGENERATED END --------------------------"
;
/** Line separator. */
private static final String
LINE_SEPARATOR =
"\t//-----------------------------------------------------------------------"
;
/** Line separator. */
private static final String
LINE_SEPARATOR_INDENTED =
"\t\t//-----------------------------------------------------------------------"
;
/** Pattern to find bean type. */ // handle three comma separated generic parameters // handle generic parameter extends clause // handle extends clause with further level of generic parameters // handle extends clause union types without generic parameters
private static final Pattern
BEAN_TYPE =
Pattern. compile
(
".*class +(" + "([A-Z][A-Za-z0-9_]+)" + "(?:<" + "([A-Z])( +extends +[A-Za-z0-9_]+(?:[<][A-Za-z0-9_, ?]+[>])?(?:[ ]+[&][ ]+[A-Za-z0-9]+)*)?" + "(?:[,] +" + "([A-Z])( +extends +[A-Za-z0-9_]+(?:[<][A-Za-z0-9_, ?]+[>])?(?:[ ]+[&][ ]+[A-Za-z0-9]+)*)?" + "(?:[,] +" + "([A-Z])( +extends +[A-Za-z0-9_]+(?:[<][A-Za-z0-9_, ?]+[>])?(?:[ ]+[&][ ]+[A-Za-z0-9]+)*)?" + ")?" + ")?" + ">)?" + ").*"
)
;
/** Pattern to find super type. */
private static final Pattern
SUPER_TYPE =
Pattern. compile
(
".*extends +(" + "([A-Z][A-Za-z0-9_]+)" + "(?:<" + "([A-Z][A-Za-z0-9_<> ]*)" + "(?:[,] +" + "([A-Z][A-Za-z0-9_<> ]*)?" + "(?:[,] +" + "([A-Z][A-Za-z0-9_<> ]*)?" + ")?" + ")?" + ">)?" + ").*"
)
;
/** Pattern to find root type. */
private static final Pattern
SUPER_IMPL_TYPE =
Pattern. compile
(
".*implements.*[ ,]((Immutable)?Bean)[ ,].*"
)
;
/** Pattern to find serializable interface. */
private static final Pattern
SERIALIZABLE_TYPE =
Pattern. compile
(
".*implements.*[ ,]Serializable[ ,].*"
)
;
/** The style pattern. */
private static final Pattern
STYLE_PATTERN =
Pattern. compile
(
".*[ ,(]style[ ]*[=][ ]*[\"]([a-zA-Z]*)[\"].*"
)
;
/** The builderScope pattern. */
private static final Pattern
BUILDER_SCOPE_PATTERN =
Pattern. compile
(
".*[ ,(]builderScope[ ]*[=][ ]*[\"]([a-zA-Z]*)[\"].*"
)
;
/** The hierarchy pattern. */
private static final Pattern
HIERARCHY_PATTERN =
Pattern. compile
(
".*[ ,(]hierarchy[ ]*[=][ ]*[\"]([a-zA-Z]*)[\"].*"
)
;
/** The cacheHashCode pattern. */
private static final Pattern
CACHE_HASH_CODE_PATTERN =
Pattern. compile
(
".*[ ,(]cacheHashCode[ ]*[=][ ]*(true|false).*"
)
;
/** The validator pattern. */
private static final Pattern
VALIDATOR_PATTERN =
Pattern. compile
(
".*private[ ]+void[ ]+" + "([a-zA-Z][a-zA-Z0-9]*)[(][ ]*[)].*"
)
;
/** The defaults pattern. */
private static final Pattern
DEFAULTS_PATTERN =
Pattern. compile
(
".*private[ ]+static[ ]+void[ ]+" + "([a-zA-Z][a-zA-Z0-9]*)[(][ ]*Builder[ ]+[a-zA-Z][a-zA-Z0-9]*[ ]*[)].*"
)
;
/** Pattern to find super type. */
private static final
Set <
String
>
PRIMITIVE_EQUALS =
new
HashSet <
String
>
()
;
static {
PRIMITIVE_EQUALS. add
(
"boolean"
)
;
PRIMITIVE_EQUALS. add
(
"char"
)
;
PRIMITIVE_EQUALS. add
(
"byte"
)
;
PRIMITIVE_EQUALS. add
(
"short"
)
;
PRIMITIVE_EQUALS. add
(
"int"
)
;
PRIMITIVE_EQUALS. add
(
"long"
)
;
// not float or double, as Double.equals is not the same as double == }
/** The content to process. */
private final
List <
String
>
