/** * Constructor. * @param content the content to process, not null * @param config the config to use, not null */
BeanGen (
List <
String
>
content
,
BeanGenConfig
config
)
{
this. content
= content
;
this. config
= config
;
int
beanDefIndex =
parseBeanDefinition ()
;
if (
beanDefIndex >= 0
)
{
this. data
= new
GeneratableBean ()
;
this.data. getCurrentImports
()
.
addAll (
parseImports (
beanDefIndex
)
)
;
this.data. setImportInsertLocation
(
parseImportLocation (
beanDefIndex
)
)
;
this.data. setBeanStyle
(
parseBeanStyle (
beanDefIndex
)
)
;
if (
data. isBeanStyleValid
()
== false
)
{ throw new
RuntimeException (
"Invalid bean style: " +
data. getBeanStyle
()
)
;
}
this.data. setBeanBuilderScope
(
parseBeanBuilderScope (
beanDefIndex
)
)
;
if (
data. isBeanBuilderScopeValid
()
== false
)
{ throw new
RuntimeException (
"Invalid bean builder scope: " +
data. getBeanStyle
()
)
;
}
this.data. setCacheHashCode
(
parseCacheHashCode (
beanDefIndex
)
)
;
this.data. setImmutableConstructor
(
