beanDefIndex
)
)
;
this.data. setSerializable
(
parseSerializable (
beanDefIndex
)
)
;
if (
parseBeanHierarchy (
beanDefIndex
)
.
equals (
"immutable"
)
)
{
this.data. setImmutable
(
true
)
;
this.data. setConstructorStyle
(
CONSTRUCTOR_BY_BUILDER
)
;
}
else if (
this.data. getImmutableConstructor
()
== CONSTRUCTOR_NONE
)
{ if (
data. isImmutable
()
)
{ if (
data. isTypeFinal
()
)
{
this. data. setConstructorStyle
(
CONSTRUCTOR_BY_ARGS
)
;
}
else {
this. data. setConstructorStyle
(
CONSTRUCTOR_BY_BUILDER
)
;
}
}
else {
this.data. setConstructorStyle
(
CONSTRUCTOR_BY_BUILDER
)
;
}
}
else {
this.data. setConstructorStyle
(
this. data. getImmutableConstructor
()
)
;
}
if (
data. isImmutable
()
)
{
this.data. setImmutableValidator
(
parseImmutableValidator (
beanDefIndex
)
)
;
this.data. setImmutableDefaults
(
parseImmutableDefaults (
beanDefIndex
)
)
;
this.data. setImmutablePreBuild
(
parseImmutablePreBuild (
beanDefIndex
)
)
;
}
this. properties
=
parseProperties (
data
)
;
this. autoStartIndex
=
parseStartAutogen ()
;
this. autoEndIndex
=
parseEndAutogen ()
;
this. insertRegion
=
content. subList
(
autoStartIndex + 1
,
autoEndIndex
)
;
this.data. setManualSerializationId
(
parseManualSerializationId (
beanDefIndex
)
)
;
this.data. setManualClone
(
parseManualClone (
beanDefIndex
)
)
;
this.data. setManualEqualsHashCode
(
parseManualEqualsHashCode (
beanDefIndex
)
)
;
this.data. setManualToStringCode
(
parseManualToStringCode (
beanDefIndex
)
)
;
if (
data. isImmutable
()
)
{ for (
PropertyGen
prop :
properties
) { if (
prop. getData
()
.
isDerived ()
== false &&
prop. getData
()
.
isFinal ()
== false
)
{ throw new
RuntimeException (
"ImmutableBean must have final properties: " +
data. getTypeRaw
()
+ "." +
prop. getData
()
.
getFieldName ()
)
;
}
}
}
else if (
data. getImmutableConstructor
()
> CONSTRUCTOR_NONE
)
{ throw new
RuntimeException (
"Mutable beans must not specify @ImmutableConstructor: " +
data. getTypeRaw
()
)
;
}
if (
data. isCacheHashCode
()
)
{
data. setCacheHashCode
(
data. isImmutable
()
&&
data. isManualEqualsHashCode
()
== false
)
;
}
}
else {
this. autoStartIndex
= -1
;
this. autoEndIndex
= -1
;
this. insertRegion
= null
;
this. data
= null
;
this. properties
= null
;
}
}
//-----------------------------------------------------------------------
void
process () { if (
insertRegion != null
)
{
data. ensureImport
(
BeanDefinition.
class
)
;
if (
properties. size
()
> 0
)
{
data. ensureImport
(
PropertyDefinition.
class
)
;
}
removeOld ()
;
if (
data. isRootClass
()
&&
data. isExtendsDirectBean
()
)
{
data. ensureImport
(
DirectBean.
class
)
;
}
insertRegion. add
(
"\t///CLOVER:OFF"
)
;
generateMeta ()
;
generateSerializationVersionId ()
;
generateHashCodeField ()
;
generateImmutableBuilderMethod ()
;
generateArgBasedConstructor ()
;
generateBuilderBasedConstructor ()
;
generateMetaBean ()
;
generatePropertyByName ()
;
generatePropertyNames ()
;
generateGettersSetters ()
;
generateSeparator ()
;
generateImmutableToBuilder ()
;
generateClone ()
;
generateEquals ()
;
generateHashCode ()
;
generateToString ()
;
generateMetaClass ()
;
generateBuilderClass ()
;
insertRegion. add
(
"\t///CLOVER:ON"
)
;
resolveImports ()
;
resolveIndents ()
;
}
}
private void
resolveImports () { if (
data. getNewImports
()
.
size ()
> 0
)
{
int
pos =
data. getImportInsertLocation
()
+ 1
;
for (
String
imp :
data. getNewImports
()
) {
content. add
(
pos++
,
"import " + imp + ";"
)
;
}
}
}
private void
resolveIndents () { for (
ListIterator <
String
>
it =
content. listIterator
()
;
it. hasNext
()
;
) {
it. set
(
it. next
()
.
replace (
"\t"
,
config. getIndent
()
)
)
;
}
}
