private String []
parseBeanType (
int
defLine
)
{
Matcher
matcher =
BEAN_TYPE. matcher
(
""
)
;
for (
int
index =
defLine
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
;
matcher. reset
(
line
)
;
if (
matcher. matches
()
)
{
String
startStr =
line. substring
(
0
,
matcher. start
(
1
)
)
;
String
fnl =
startStr. contains
(
" final "
)
||
startStr. startsWith
(
"final "
)
? "final" : null
;
return new
String []
{
fnl
,
matcher. group
(
1
)
,
matcher. group
(
2
)
,
matcher. group
(
3
)
,
matcher. group
(
4
)
,
matcher. group
(
5
)
,
matcher. group
(
6
)
,
matcher. group
(
7
)
,
matcher. group
(
8
)
}
;
}
if (
line. contains
(
AUTOGENERATED_START_TEXT
)
)
{ break; }
}
throw new
RuntimeException (
"Unable to locate bean class name"
)
;
}
private String []
parseBeanSuperType (
int
defLine
)
{ // need to start searching beyond the full type to avoid 'extends' having two meanings
String
fullType =
data. getType
()
;
// search for implements
Matcher
matcherImplements =
SUPER_IMPL_TYPE. matcher
(
""
)
;
boolean
matchedType =
false
;
for (
int
index =
defLine
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
;
if (
matchedType == false
)
{ if (
line. contains
(
fullType
)
== false
)
{ continue; }
matchedType = true
;
line =
line. substring
(
line. indexOf
(
fullType
)
+
fullType. length
()
)
;
}
matcherImplements. reset
(
line
)
;
if (
matcherImplements. matches
()
)
{ return new
String []
{
matcherImplements. group
(
1
)
}
;
}
if (
line. contains
(
AUTOGENERATED_START_TEXT
)
)
{ break; }
}
// search for extends
Matcher
matcherExtends =
SUPER_TYPE. matcher
(
""
)
;
matchedType = false
;
for (
int
index =
defLine
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
;
if (
matchedType == false
)
{ if (
line. contains
(
fullType
)
== false
)
{ continue; }
matchedType = true
;
line =
line. substring
(
line. indexOf
(
fullType
)
+
fullType. length
()
)
;
}
matcherExtends. reset
(
line
)
;
if (
matcherExtends. matches
()
)
{ return new
String []
{
matcherExtends. group
(
1
)
,
matcherExtends. group
(
2
)
,
matcherExtends. group
(
3
)
,
matcherExtends. group
(
4
)
,
matcherExtends. group
(
5
)
}
;
}
if (
line. contains
(
AUTOGENERATED_START_TEXT
)
)
{ break; }
}
throw new
RuntimeException (
"Unable to locate bean superclass"
)
;
}
