private int
parseImmutableConstructor (
int
defLine
)
{
int
found =
CONSTRUCTOR_NONE
;
for (
int
index =
defLine
;
index <
content. size
()
;
index++
) { if (
content. get
(
index
)
.
trim ()
.
equals (
"@ImmutableConstructor"
)
)
{ if (
found > 0
)
{ throw new
RuntimeException (
"Only one @ImmutableConstructor may be specified"
)
;
}
found = CONSTRUCTOR_BY_ARGS
;
if (
index + 1 <
content. size
()
)
{
String
nextLine =
content. get
(
index + 1
)
;
if (
nextLine. contains
(
"Builder "
)
||
nextLine. contains
(
"Builder<"
)
)
{
found = CONSTRUCTOR_BY_BUILDER
;
}
}
}
}
return
found
;
}
private String
parseImmutableValidator (
int
defLine
)
{
boolean
found =
false
;
for (
int
index =
defLine
;
index <
content. size
()
;
index++
) { if (
content. get
(
index
)
.
trim ()
.
equals (
"@ImmutableValidator"
)
)
{ if (
found
)
{ throw new
RuntimeException (
"Only one @ImmutableValidator may be specified"
)
;
}
found = true
;
if (
index + 1 <
content. size
()
)
{
String
nextLine =
content. get
(
index + 1
)
;
Matcher
matcher =
VALIDATOR_PATTERN. matcher
(
nextLine
)
;
if (
matcher. matches
()
)
{ return
matcher. group
(
1
)
;
}
throw new
RuntimeException (
"@ImmutableValidator method must be private void and no-args"
)
;
}
}
}
return
null
;
}
