index + 1
)
;
Matcher
matcher =
DEFAULTS_PATTERN. matcher
(
nextLine
)
;
if (
matcher. matches
()
)
{ return
matcher. group
(
1
)
;
}
throw new
RuntimeException (
"@ImmutablePreBuild method must be private static void and have one argument of type 'Builder'"
)
;
}
}
}
return
null
;
}
private
List <
PropertyGen
>
parseProperties (
GeneratableBean
data
)
{
List <
PropertyGen
>
props =
new
ArrayList <
PropertyGen
>
()
;
for (
int
index =
0
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
.
trim ()
;
if (
line. startsWith
(
"@PropertyDefinition"
)
)
{
PropertyGen
prop =
new
PropertyGen (
this
,
content
,
index
,
false
)
;
props. add
(
prop
)
;
data. getProperties
()
.
add (
prop. getData
()
)
;
}
else if (
line. startsWith
(
"@DerivedProperty"
)
)
{
PropertyGen
prop =
new
PropertyGen (
this
,
content
,
index
,
true
)
;
props. add
(
prop
)
;
data. getProperties
()
.
add (
prop. getData
()
)
;
}
}
return
props
;
}
private int
parseStartAutogen () { for (
int
index =
0
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
.
trim ()
;
if (
line. contains
(
" AUTOGENERATED START "
)
)
{
content. set
(
index
,
AUTOGENERATED_START
)
;
return
index
;
}
}
for (
int
index =
content. size
()
- 1
;
index >= 0
;
index--
) {
String
line =
content. get
(
index
)
.
trim ()
;
if (
line. equals
(
"}"
)
)
{
content. add
(
index
,
AUTOGENERATED_START
)
;
return
index
;
}
if (
line. length
()
> 0
)
{ break; }
}
throw new
RuntimeException (
"Unable to locate start autogeneration point"
)
;
}
private int
parseEndAutogen () { for (
int
index =
autoStartIndex
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
.
trim ()
;
if (
line. contains
(
" AUTOGENERATED END "
)
)
{
content. set
(
index
,
AUTOGENERATED_END
)
;
return
index
;
}
}
content. add
(
autoStartIndex + 1
,
AUTOGENERATED_END
)
;
return
autoStartIndex + 1
;
}
private void
removeOld () {
insertRegion. clear
()
;
}
private boolean
parseManualClone (
int
defLine
)
{ for (
int
index =
defLine
;
index < autoStartIndex
;
index++
) {
String
line =
content. get
(
index
)
.
trim ()
;
if (
line. startsWith
(
"public "
)
&&
line. endsWith
(
" clone() {"
)
)
{ return true;
}
}
for (
int
index =
autoEndIndex
;
index <
content. size
()
;
index++
) {
String
line =
content. get
(
index
)
.
trim ()
;
if (
line. startsWith
(
"public "
)
&&
line. endsWith
(
" clone() {"
)
)
{ return true;
}
}
return false;
}
