private void
generateImmutableBuilderMethod () { if (
data. isConstructable
()
&& ((
data. isImmutable
()
&&
data. isEffectiveBuilderScopePublic
()
) || (
data. isMutable
()
&&
data. isBuilderScopePublic
()
))
)
{
insertRegion. add
(
"\t/**"
)
;
insertRegion. add
(
"\t * Returns a builder used to create an instance of the bean."
)
;
if (
data. isTypeGeneric
()
)
{ for (
int
j =
0
;
j <
data. getTypeGenericCount
()
;
j++
) {
insertRegion. add
(
