else {
insertRegion. add
(
"\tpublic static " +
data. getTypeRaw
()
+ ".Builder builder() {"
)
;
}
insertRegion. add
(
"\t\treturn new " +
data. getTypeRaw
()
+ ".Builder" +
data. getTypeGenericName
(
true
)
+ "();"
)
;
insertRegion. add
(
"\t}"
)
;
insertRegion. add
(
""
)
;
}
}
private void
generateBuilderBasedConstructor () { if (
data. getConstructorStyle
()
== CONSTRUCTOR_BY_BUILDER &&
data. getImmutableConstructor
()
== CONSTRUCTOR_NONE && ((
data. isMutable
()
&&
data. isBuilderScopePublic
()
) ||
data. isImmutable
()
)
)
{
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
String
scope =
(
data. isTypeFinal
()
? "private" : "protected")
;
// signature
insertRegion. add
(
"\t/**"
)
;
insertRegion. add
(
"\t * Restricted constructor."
)
;
insertRegion. add
(
"\t * @param builder the builder to copy from, not null"
)
;
insertRegion. add
(
"\t */"
)
;
insertRegion. add
(
"\t" + scope + " " +
data. getTypeRaw
()
+ "(" +
data. getTypeRaw
()
+ ".Builder" +
data. getTypeGenericName
(
true
)
+ " builder) {"
)
;
// super if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\tsuper(builder);"
)
;
}
// validate for (
PropertyGen
prop :
properties
) { if (
prop. getData
()
.
isValidated ()
)
{
insertRegion. add
(
"\t\t" +
prop. getData
()
.
getValidationMethodName ()
+ "(builder." +
prop. generateBuilderFieldName
()
+ ", \"" +
prop. getData
()
.
getPropertyName ()
+ "\");"
)
;
}
}
// assign if (
data. isImmutable
()
)
{ // assign for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
insertRegion. addAll
(
nonDerived. get
(
i
)
.
generateConstructorAssign (
"builder."
)
)
;
}
}
else { for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
PropertyGen
propGen =
nonDerived. get
(
i
)
;
GeneratableProperty
prop =
propGen. getData
()
;
if (
prop. isCollectionType
()
)
{ if (
prop. isNotNull
()
)
{
insertRegion. add
(
"\t\tthis." +
prop.
getPropertyName
()
+ ".addAll(builder." +
propGen .
generateBuilderFieldName
()
+ ");"
)
;
}
else {
insertRegion. add
(
"\t\tif (" +
prop.
getPropertyName
()
+ " != null) {"
)
;
insertRegion. add
(
"\t\t\tthis." +
prop.
getPropertyName
()
+ ".addAll(builder." +
propGen .
generateBuilderFieldName
()
+ ");"
)
;
insertRegion. add
(
"\t\t}"
)
;
}
}
else if (
prop. isMapType
()
)
{ if (
prop. isNotNull
()
)
{
insertRegion. add
(
"\t\tthis." +
prop .
getPropertyName
()
+ ".putAll(builder." +
propGen .
generateBuilderFieldName
()
+ ");"
)
;
}
else {
insertRegion. add
(
"\t\tif (" +
prop .
getPropertyName
()
+ " != null) {"
)
;
insertRegion. add
(
"\t\t\tthis." +
prop .
getPropertyName
()
+ ".putAll(builder." +
propGen .
generateBuilderFieldName
()
+ ");"
)
;
insertRegion. add
(
"\t\t}"
)
;
}
}
else {
insertRegion. add
(
"\t\tthis." +
prop.
getPropertyName
()
+ " = builder." +
propGen.
generateBuilderFieldName
()
+ ";"
)
;
}
}
}
if (
data. getImmutableValidator
()
!= null
)
{
insertRegion. add
(
"\t\t" +
data. getImmutableValidator
()
+ "();"
)
;
}
insertRegion. add
(
"\t}"
)
;
insertRegion. add
(
""
)
;
}
}
private void
