}
if (
data. getImmutableValidator
()
!= null
)
{
insertRegion. add
(
"\t\t" +
data. getImmutableValidator
()
+ "();"
)
;
}
insertRegion. add
(
"\t}"
)
;
insertRegion. add
(
""
)
;
}
