private void
generateImmutableToBuilder () { if (
data. isImmutable
()
&&
data. isEffectiveBuilderScopePublic
()
)
{ if (
data. isConstructable
()
)
{
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
if (
nonDerived. size
()
> 0
)
{
insertRegion. add
(
"\t/**"
)
;
