private void
generateEquals () { if (
data. isManualEqualsHashCode
()
)
{ return; }
data. ensureImport
(
JodaBeanUtils.
class
)
;
insertRegion. add
(
"\t@Override"
)
;
insertRegion. add
(
"\tpublic boolean equals(Object obj) {"
)
;
insertRegion. add
(
"\t\tif (obj == this) {"
)
;
insertRegion. add
(
"\t\t\treturn true;"
)
;
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
"\t\tif (obj != null && obj.getClass() == this.getClass()) {"
)
;
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
if (
nonDerived. size
()
== 0
)
{ if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t\treturn super.equals(obj);"
)
;
}
else {
insertRegion. add
(
"\t\t\treturn true;"
)
;
}
}
else {
insertRegion. add
(
"\t\t\t" +
data. getTypeWildcard
()
+ " other = (" +
data. getTypeWildcard
()
+ ") obj;"
)
;
for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
PropertyGen
prop =
nonDerived. get
(
i
)
;
String
getter =
prop. getData
()
.
getGetterGen ()
.
generateGetInvoke (
prop. getData
()
)
;
String
equals =
"JodaBeanUtils.equal(" + getter + ", other." + getter + ")"
;
if (
PRIMITIVE_EQUALS. contains
(
prop. getData
()
.
getType ()
)
)
{
equals = "(" + getter + " == other." + getter + ")"
;
}
insertRegion. add
(
(i == 0 ? "\t\t\treturn " : "\t\t\t\t\t") + equals + (
data. isSubClass
()
|| i <
nonDerived. size
()
- 1 ? " &&" : ";")
)
;
}
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t\t\t\tsuper.equals(obj);"
)
;
}
}
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
"\t\treturn false;"
)
;
insertRegion. add
(
"\t}"
)
;
insertRegion. add
(
""
)
;
}
private void
generateHashCode () { if (
data. isManualEqualsHashCode
()
)
{ return; }
data. ensureImport
(
JodaBeanUtils.
class
)
;
insertRegion. add
(
"\t@Override"
)
;
insertRegion. add
(
"\tpublic int hashCode() {"
)
;
if (
data. isCacheHashCode
()
)
{
insertRegion. add
(
"\t\tint hash = cachedHashCode;"
)
;
insertRegion. add
(
"\t\tif (hash == 0) {"
)
;
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t\thash = 7;"
)
;
}
else {
insertRegion. add
(
"\t\t\thash = getClass().hashCode();"
)
;
}
generateHashCodeContent (
"\t\t\t"
)
;
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t\thash = hash ^ super.hashCode();"
)
;
}
insertRegion. add
(
"\t\t\tcachedHashCode = hash;"
)
;
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
"\t\treturn hash;"
)
;
}
else { if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\tint hash = 7;"
)
;
}
else {
insertRegion. add
(
"\t\tint hash = getClass().hashCode();"
)
;
}
generateHashCodeContent (
"\t\t"
)
;
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\treturn hash ^ super.hashCode();"
)
;
}
else {
insertRegion. add
(
"\t\treturn hash;"
)
;
}
}
insertRegion. add
(
"\t}"
)
;
insertRegion. add
(
""
)
;
}
private void
generateHashCodeContent (
String
indent
)
{
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
PropertyGen
prop =
nonDerived. get
(
i
)
;
String
getter =
prop. getData
()
.
getGetterGen ()
.
generateGetInvoke (
prop. getData
()
)
;
insertRegion. add
(
indent + "hash = hash * 31 + JodaBeanUtils.hashCode(" + getter + ");"
)
;
}
}
