generateMetaValidate () { if (
data. isValidated
()
== false ||
data. isImmutable
()
)
{ return; }
data. ensureImport
(
Bean.
class
)
;
insertRegion. add
(
"\t\t@Override"
)
;
insertRegion. add
(
"\t\tprotected void validate(Bean bean) {"
)
;
if (
data. isValidated
()
)
{ for (
PropertyGen
prop :
properties
) { if (
prop. getData
()
.
isValidated ()
)
{
insertRegion. add
(
"\t\t\t" +
prop. getData
()
.
getValidationMethodName ()
+ "(((" +
data. getTypeWildcard
()
+ ") bean)." +
prop. getData
()
.
getFieldName ()
+ ", \"" +
prop. getData
()
.
getPropertyName ()
+ "\");"
)
;
}
}
}
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t\tsuper.validate(bean);"
)
;
}
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
""
)
;
}
