private void
generateBuilderClass () { if (
data. isMutable
()
&&
data. isBuilderScopePublic
()
== false
)
{ return; }
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
generateSeparator ()
;
String
finalType =
data. isTypeFinal
()
? "final " : ""
;
insertRegion. add
(
"\t/**"
)
;
insertRegion. add
(
"\t * The bean-builder for {@code " +
data. getTypeRaw
()
+ "}."
)
;
if (
data. isTypeGeneric
()
)
{ for (
int
j =
0
;
j <
data. getTypeGenericCount
()
;
j++
) {
insertRegion. add
(
"\t * @param " +
data. getTypeGenericName
(
j
,
true
)
+ " the type"
)
;
}
}
insertRegion. add
(
"\t */"
)
;
String
superBuilder
;
if (
data. isSubClass
()
)
{
superBuilder =
data. getSuperTypeRaw
()
+ ".Builder" +
data. getSuperTypeGeneric
(
true
)
;
}
else {
data. ensureImport
(
DirectFieldsBeanBuilder.
class
)
