else {
insertRegion. add
(
"\t" +
data. getEffectiveBuilderScope
()
+ " abstract static " + finalType + "class Builder" +
data. getTypeGeneric
(
true
)
+ " extends " + superBuilder + " {"
)
;
}
if (
nonDerived. size
()
> 0
)
{
insertRegion. add
(
""
)
;
generateBuilderProperties ()
;
}
insertRegion. add
(
""
)
;
generateBuilderConstructorNoArgs ()
;
generateBuilderConstructorCopy ()
;
generateIndentedSeparator ()
;
generateBuilderGet ()
;
