private void
generateBuilderSet () {
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
boolean
generics =
false
;
for (
GeneratableProperty
prop :
data. getProperties
()
) {
generics |= (
prop. isGeneric
()
&&
prop. isGenericWildcardParamType
()
== false)
;
}
if (
generics
)
{
insertRegion. add
(
"\t\t@SuppressWarnings(\"unchecked\")"
)
;
}
insertRegion. add
(
"\t\t@Override"
)
;
insertRegion. add
(
"\t\tpublic Builder" +
data. getTypeGenericName
(
true
)
+ " set(String propertyName, Object newValue) {"
)
;
if (
nonDerived. size
()
> 0
)
{
insertRegion. add
(
"\t\t\tswitch (propertyName.hashCode()) {"
)
;
for (
PropertyGen
prop :
nonDerived
) {
insertRegion. addAll
(
prop. generateBuilderFieldSet
()
)
;
}
insertRegion. add
(
"\t\t\t\tdefault:"
)
;
if (
data. isRootClass
()
)
{
data. ensureImport
(
NoSuchElementException.
class
)
;
insertRegion. add
(
"\t\t\t\t\tthrow new NoSuchElementException(\"Unknown property: \" + propertyName);"
)
;
}
else {
insertRegion. add
(
"\t\t\t\t\tsuper.set(propertyName, newValue);"
)
;
insertRegion. add
(
"\t\t\t\t\tbreak;"
)
;
}
insertRegion. add
(
"\t\t\t}"
)
;
insertRegion. add
(
"\t\t\treturn this;"
)
;
}
else {
data. ensureImport
(
NoSuchElementException.
class
)
;
insertRegion. add
(
"\t\t\tthrow new NoSuchElementException(\"Unknown property: \" + propertyName);"
)
;
}
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
""
)
;
}
