private void
generateBuilderBuilder () {
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
insertRegion. add
(
"\t\t@Override"
)
;
insertRegion. add
(
"\t\tpublic " +
data. getTypeRaw
()
+
data. getTypeGenericName
(
true
)
+ " build() {"
)
;
if (
data. getImmutablePreBuild
()
!= null
)
{
insertRegion. add
(
"\t\t\t" +
data. getImmutablePreBuild
()
+ "(this);"
)
;
}
if (
data. getConstructorStyle
()
== CONSTRUCTOR_BY_ARGS
)
{ if (
nonDerived. size
()
== 0
)
{
insertRegion. add
(
"\t\t\treturn new " +
data. getTypeRaw
()
+
data. getTypeGenericName
(
true
)
+ "();"
)
;
}
else {
insertRegion. add
(
"\t\t\treturn new " +
data. getTypeRaw
()
+
data. getTypeGenericName
(
true
)
+ "("
)
;
for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
insertRegion. add
(
"\t\t\t\t\t" +
nonDerived. get
(
i
)
.
generateBuilderFieldName ()
+ (i <
nonDerived. size
()
- 1 ? "," : ");")
)
;
}
}
}
else if (
data. getConstructorStyle
()
== CONSTRUCTOR_BY_BUILDER
)
{
insertRegion. add
(
"\t\t\treturn new " +
data. getTypeRaw
()
+
data. getTypeGenericName
(
true
)
+ "(this);"
)
;
}
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
""
)
;
}
