private void
generateBuilderToString () {
List <
PropertyGen
>
nonDerived =
nonDerivedProperties ()
;
if (
data. isImmutable
()
&&
data. isTypeFinal
()
)
{
insertRegion. add
(
"\t\t@Override"
)
;
insertRegion. add
(
"\t\tpublic String toString() {"
)
;
if (
nonDerived. size
()
== 0
)
{
insertRegion. add
(
"\t\t\treturn \"" +
data. getTypeRaw
()
+ ".Builder{}\";"
)
;
}
else {
insertRegion. add
(
"\t\t\tStringBuilder buf = new StringBuilder(" + (
nonDerived. size
()
* 32 + 32) + ");"
)
;
insertRegion. add
(
"\t\t\tbuf.append(\"" +
data. getTypeRaw
()
+ ".Builder{\");"
)
;
for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
PropertyGen
prop =
nonDerived. get
(
i
)
;
String
getter =
nonDerived. get
(
i
)
.
generateBuilderFieldName ()
;
String
base =
"\t\t\tbuf.append(\"" +
prop. getData
()
.
getPropertyName ()
+ "\").append('=').append(JodaBeanUtils.toString(" + getter + "))"
;
if (
i <
nonDerived. size
()
- 1
)
{
insertRegion. add
(
base + ".append(',').append(' ');"
)
;
}
else {
insertRegion. add
(
base + ";"
)
;
}
}
insertRegion. add
(
"\t\t\tbuf.append('}');"
)
;
insertRegion. add
(
"\t\t\treturn buf.toString();"
)
;
}
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
""
)
;
return; }
insertRegion. add
(
"\t\t@Override"
)
;
insertRegion. add
(
"\t\tpublic String toString() {"
)
;
insertRegion. add
(
"\t\t\tStringBuilder buf = new StringBuilder(" + (
nonDerived. size
()
* 32 + 32) + ");"
)
;
insertRegion. add
(
"\t\t\tbuf.append(\"" +
data. getTypeRaw
()
+ ".Builder{\");"
)
;
insertRegion. add
(
"\t\t\tint len = buf.length();"
)
;
insertRegion. add
(
"\t\t\ttoString(buf);"
)
;
insertRegion. add
(
"\t\t\tif (buf.length() > len) {"
)
;
insertRegion. add
(
"\t\t\t\tbuf.setLength(buf.length() - 2);"
)
;
insertRegion. add
(
"\t\t\t}"
)
;
insertRegion. add
(
"\t\t\tbuf.append('}');"
)
;
insertRegion. add
(
"\t\t\treturn buf.toString();"
)
;
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
""
)
;
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t@Override"
)
;
}
insertRegion. add
(
"\t\tprotected void toString(StringBuilder buf) {"
)
;
if (
data. isSubClass
()
)
{
insertRegion. add
(
"\t\t\tsuper.toString(buf);"
)
;
}
for (
int
i =
0
;
i <
nonDerived. size
()
;
i++
) {
PropertyGen
prop =
nonDerived. get
(
i
)
;
String
getter =
nonDerived. get
(
i
)
.
generateBuilderFieldName ()
;
insertRegion. add
(
"\t\t\tbuf.append(\"" +
prop. getData
()
.
getPropertyName ()
+ "\").append('=').append(JodaBeanUtils.toString(" + getter + ")).append(',').append(' ');"
)
;
}
insertRegion. add
(
"\t\t}"
)
;
insertRegion. add
(
""
)
;
}
//-----------------------------------------------------------------------
boolean
isBean () { return
data != null
;
}
GeneratableBean
getData () { return
data
;
}
BeanGenConfig
getConfig () { return
config
;
}
String
getFieldPrefix () { return
config. getPrefix
()
;
}
private
List <
PropertyGen
>
nonDerivedProperties () {
List <
PropertyGen
>
nonDerived =
new
ArrayList <
PropertyGen
>
()
;
for (
PropertyGen
prop :
properties
) { if (
prop. getData
()
.
isDerived ()
== false
)
{
nonDerived. add
(
prop
)
;
}
}
return
nonDerived
;
}
