abstract class GetterGen {
/** * Generates the getter method. * @param prop the property data, not null * @return the generated code, not null */
abstract
List <
String
>
generateGetter (
GeneratableProperty
prop
)
;
/** * Generates the getter method invocation. * This is just the method name. * @param prop the property data, not null * @return the generated code, not null */
String
generateGetInvoke (
GeneratableProperty
prop
)
{ return "get" +
prop. getUpperName
()
+ "()"
;
}
