private static
List <
String
>
doGenerateGetter (
GeneratableProperty
prop
,
String
access
,
String
prefix
,
String
expression
)
{
List <
String
>
list =
new
ArrayList <
String
>
()
;
list. add
(
"\t/**"
)
;
list. add
(
"\t * Gets " +
prop. getFirstComment
()
)
;
for (
String
comment :
prop. getComments
()
) {
list. add
(
"\t * " + comment
)
;
}
list. add
(
"\t * @return the value of the property" +
prop. getNotNullJavadoc
()
)
;
list. add
(
"\t */"
)
;
if (
prop. isOverrideGet
()
)
{
list. add
(
"\t@Override"
)
;
}
if (
prop. isDeprecated
()
)
{
list. add
(
"\t@Deprecated"
)
;
}
list. add
(
"\t" + access + " " +
prop. getType
()
+ " " + prefix +
prop. getUpperName
()
+ "() {"
)
;
list. add
(
"\t\treturn " + expression + ";"
)
;
list. add
(
"\t}"
)
;
list. add
(
""
)
;
return
list
;
}
