public void
test_readWrite_primitives () throws
IOException
{
ByteArrayOutputStream
baos =
new
ByteArrayOutputStream ()
;
DataOutputStream
out =
new
DataOutputStream (
baos
)
;
out. writeByte
(
MsgPack. MIN_FIX_ARRAY
+ 2
)
;
out. writeByte
(
1
)
;
out. writeByte
(
MsgPack. MIN_FIX_MAP
+ 6
)
;
out. writeByte
(
MsgPack. MIN_FIX_STR
+ 3
)
;
out. writeBytes
(
"tru"
)
;
out. writeByte
(
MsgPack. TRUE
)
;
out. writeByte
(
MsgPack. MIN_FIX_STR
+ 3
)
;
out. writeBytes
(
"fal"
)
;
out. writeByte
(
MsgPack. FALSE
)
;
out. writeByte
(
MsgPack. MIN_FIX_STR
+ 3
)
;
out. writeBytes
(
"byt"
)
;
out. writeByte
(
MsgPack. MIN_FIX_MAP
+ 1
)
;
out. writeByte
(
MsgPack. EXT_8
)
;
out. writeByte
(
4
)
;
out. writeByte
(
MsgPack. JODA_TYPE_DATA
)
;
out. writeBytes
(
"Byte"
)
;
out. writeByte
(
1
)
;
out. writeByte
(
MsgPack. MIN_FIX_STR
+ 3
)
;
out. writeBytes
(
"sht"
)
;
out. writeByte
(
MsgPack. MIN_FIX_MAP
+ 1
)
;
out. writeByte
(
MsgPack. EXT_8
)
;
out. writeByte
(
5
)
;
out. writeByte
(
MsgPack. JODA_TYPE_DATA
)
;
out. writeBytes
(
"Short"
)
;
out. writeByte
(
2
)
;
out. writeByte
(
MsgPack. MIN_FIX_STR
+ 3
)
;
out. writeBytes
(
"flt"
)
;
out. writeByte
(
MsgPack. FLOAT_32
)
;
out. writeFloat
(
1.2f
)
;
out. writeByte
(
MsgPack. MIN_FIX_STR
+ 3
)
;
out. writeBytes
(
"dbl"
)
;
out. writeByte
(
MsgPack. FLOAT_64
)
;
out. writeDouble
(
1.8d
)
;
out. close
()
;
byte []
expected =
baos. toByteArray
()
;
FlexiBean
bean =
new
FlexiBean ()
;
bean. set
(
"tru"
,
Boolean. TRUE
)
;
bean. set
(
"fal"
,
Boolean. FALSE
)
;
bean. set
(
"byt"
,
Byte. valueOf
(
(byte) 1
)
)
;
bean. set
(
"sht"
,
Short. valueOf
(
(short) 2
)
)
;
bean. set
(
"flt"
,
Float. valueOf
(
1.2f
)
)
;
bean. set
(
"dbl"
,
Double. valueOf
(
1.8d
)
)
;
byte []
bytes =
JodaBeanSer.COMPACT. binWriter
()
.
write (
bean
,
false
)
;
assertEquals (
bytes
,
expected
)
;
Bean
parsed =
JodaBeanSer.COMPACT. binReader
()
.
read (
bytes
,
FlexiBean.
class
)
;
BeanAssert. assertBeanEquals
(
bean
,
parsed
)
;
}
