unit.class:
public
class
DeferredEntityReferenceImpl

    extends
        EntityReferenceImpl

    implements
        DeferredNode


{

    //
    // Constants
    //

    /** Serialization version. */



                static
                final
                long

            serialVersionUID =

                390319091370032223L


        ;


    //
    // Data
    //

    /** Node index. */



                protected
                transient
                int

            fNodeIndex

        ;


    //
    // Constructors
    //

    /**
        * This is the deferred constructor. Only the fNodeIndex is given here.
        * All other data, can be requested from the ownerDocument via the index.
        */


        DeferredEntityReferenceImpl
        (



                        DeferredDocumentImpl

                    ownerDocument


            ,



                        int

                    nodeIndex


            )

        {



                        super
                        (


                                    ownerDocument


                            ,


                                    null


                            )



                ;




                    fNodeIndex
                    =
                    nodeIndex

                ;




                        needsSyncData
                        (

                                true

                            )



                ;


            }


    // <init>(DeferredDocumentImpl,int)

    //
    // DeferredNode methods
    //

    /** Returns the node index. */


            public
            int

        getNodeIndex
        ()
        {
            return

                    fNodeIndex

                ;

            }



    //
    // Protected methods
    //

    /**
        * Synchronize the entity data. This is special because of the way
        * that the "fast" version stores the information.
        */



            protected
            void

        synchronizeData
        ()
        {

            // no need to sychronize again



                        needsSyncData
                        (

                                false

                            )



                ;


            // get the node data



                        DeferredDocumentImpl

                    ownerDocument =

                        (DeferredDocumentImpl)
                            this.
                                ownerDocument




                ;



                    name
                    =

                        ownerDocument.
                            getNodeName

                        (


                                    fNodeIndex


                            )



                ;



                    baseURI
                    =

                        ownerDocument.
                            getNodeValue

                        (


                                    fNodeIndex


                            )



                ;


            }


    // synchronizeData()

    /** Synchronize the children. */


            protected
            void

        synchronizeChildren
        ()
        {

            // no need to synchronize again



                        needsSyncChildren
                        (

                                false

                            )



                ;


            // get children



                        isReadOnly
                        (

                                false

                            )



                ;




                        DeferredDocumentImpl

                    ownerDocument =

                        (DeferredDocumentImpl)

                                ownerDocument
                                ()




                ;




                        ownerDocument.
                            synchronizeChildren

                        (


                                    this


                            ,


                                    fNodeIndex


                            )



                ;




                        setReadOnly
                        (

                                true

                            ,

                                true

                            )



                ;


            }


    // synchronizeChildren()

    }
