unit.class:
public
class
SAXParser

    extends
        AbstractSAXParser


{

    //
    // Constants
    //

    // features

    /** Feature identifier: notify built-in refereces. */



                protected
                static
                final
                String

            NOTIFY_BUILTIN_REFS =


                    Constants.
                        XERCES_FEATURE_PREFIX

                    +
                    Constants.
                        NOTIFY_BUILTIN_REFS_FEATURE




        ;


    /** Recognized features. */



                private
                static
                final
                String
                []

            RECOGNIZED_FEATURES =


                    {

                            NOTIFY_BUILTIN_REFS

                        ,
                        }




        ;


    // properties

    /** Property identifier: symbol table. */



                protected
                static
                final
                String

            SYMBOL_TABLE =


                    Constants.
                        XERCES_PROPERTY_PREFIX

                    +
                    Constants.
                        SYMBOL_TABLE_PROPERTY




        ;


    /** Property identifier: XML grammar pool. */



                protected
                static
                final
                String

            XMLGRAMMAR_POOL =


                    Constants.
                        XERCES_PROPERTY_PREFIX

                    +
                    Constants.
                        XMLGRAMMAR_POOL_PROPERTY




        ;


    /** Recognized properties. */



                private
                static
                final
                String
                []

            RECOGNIZED_PROPERTIES =


                    {

                            SYMBOL_TABLE

                        ,

                            XMLGRAMMAR_POOL

                        ,
                        }




        ;


    //
    // Constructors
    //

    /**
        * Constructs a SAX parser using the specified parser configuration.
        */


        public
        SAXParser
        (



                        XMLParserConfiguration

                    config


            )

        {



                        super
                        (


                                    config


                            )



                ;

            }


    // <init>(XMLParserConfiguration)

    /**
        * Constructs a SAX parser using the dtd/xml schema parser configuration.
        */


        public
        SAXParser
        ()
        {



                        this
                        (


                                    null


                            ,


                                    null


                            )



                ;

            }


    // <init>()

    /**
        * Constructs a SAX parser using the specified symbol table.
        */


        public
        SAXParser
        (



                        SymbolTable

                    symbolTable


            )

        {



                        this
                        (


                                    symbolTable


                            ,


                                    null


                            )



                ;

            }


    // <init>(SymbolTable)

    /**
        * Constructs a SAX parser using the specified symbol table and
        * grammar pool.
        */


        public
        SAXParser
        (



                        SymbolTable

                    symbolTable


            ,



                        XMLGrammarPool

                    grammarPool


            )

        {



                        super
                        (

                                (XMLParserConfiguration)

                                        ObjectFactory.
                                            createObject

                                        (

                                                "org.apache.xerces.xni.parser.XMLParserConfiguration"


                                            ,


                                                    "org.apache.xerces.parsers.XIncludeAwareParserConfiguration"


                                            )




                            )



                ;


            // set features



                        fConfiguration.
                            addRecognizedFeatures

                        (


                                    RECOGNIZED_FEATURES


                            )



                ;




                        fConfiguration.
                            setFeature

                        (


                                    NOTIFY_BUILTIN_REFS


                            ,

                                true

                            )



                ;


            // set properties



                        fConfiguration.
                            addRecognizedProperties

                        (


                                    RECOGNIZED_PROPERTIES


                            )



                ;

            if
                (

                        symbolTable
                        !=
                        null

                    )


                    {



                                    fConfiguration.
                                        setProperty

                                    (


                                                SYMBOL_TABLE


                                        ,


                                                symbolTable


                                        )



                            ;

                        }



            if
                (

                        grammarPool
                        !=
                        null

                    )


                    {



                                    fConfiguration.
                                        setProperty

                                    (


                                                XMLGRAMMAR_POOL


                                        ,


                                                grammarPool


                                        )



                            ;

                        }




            }


    // <init>(SymbolTable,XMLGrammarPool)

    }
